    for char in str1:
        if char in char_count:
            char_count[char] += 1
        else:
            char_count[char] = 1

    # Subtract the characters in the second string
    for char in str2:
        if char in char_count:
            char_count[char] -= 1
        else:
            return False

    # If any of the values in the char_count dictionary are not 0, then the strings are not anagrams
    for value in char_count.values():
